import React, { useState } from "react";
import firebase from "./firebase"; // Firebase setup
import BooksList from "./BooksList";

const App = () => {
  const [user, setUser] = useState(null);
  const [error, setError] = useState("");

  const handleLogin = async (email, password) => {
    try {
      await firebase.auth().signInWithEmailAndPassword(email, password);
      const currentUser = firebase.auth().currentUser;
      setUser(currentUser); // Save user details
      localStorage.setItem("token", await currentUser.getIdToken()); // Store Firebase token
    } catch (error) {
      setError(error.message);
    }
  };

  return (
    <div>
      {user ? (
        <>
          <BooksList />
          <button onClick={() => firebase.auth().signOut()}>Log Out</button>
        </>
      ) : (
        <>
          <h2>Login</h2>
          {error && <p style={{ color: "red" }}>{error}</p>}
          <form onSubmit={(e) => e.preventDefault()}>
            <input
              type="email"
              placeholder="Email"
              onChange={(e) => setUser({ email: e.target.value })}
            />
            <input
              type="password"
              placeholder="Password"
              onChange={(e) => setUser({ password: e.target.value })}
            />
            <button onClick={() => handleLogin(user.email, user.password)}>
              Login
            </button>
          </form>
        </>
      )}
    </div>
  );
};

export default App;
